#define TVO_VPHASE_FIELD1_0_VPHASE_FIELD1_FRACTION_FIELD                        (_MK_MASK_CONST(0xffff) << TVO_VPHASE_FIELD1_0_VPHASE_FIELD1_FRACTION_SHIFT)
